mapper.select:
SELECT t.*, t.color_name as colorName, t1.color16 colorHex, tb.color AS quote FROM t_basicsdatum_material_color t LEFT JOIN t_basicsdatum_colour_library t1 ON (t.company_code = t1.company_code AND t.color_code = t1.colour_code and t1.del_flag = '0') LEFT JOIN t_basicsdatum_material_price tb ON t.color_code LIKE CONCAT('%', tb.color, '%') WHERE t.company_code = #{companyCode} and t.del_flag = '0' and t.material_code = #{materialCode}
and t.status=#{status}
GROUP BY t.id

SELECT t.*, tb.width AS quote FROM t_basicsdatum_material_width t LEFT JOIN t_basicsdatum_specification t1 ON t.company_code = t1.company_code AND t.width_code = t1.`code` AND t1.del_flag = '0' LEFT JOIN t_basicsdatum_material_price tb ON tb.width LIKE CONCAT('%', t.width_code, '%') WHERE t.company_code = #{companyCode} and t.del_flag = '0' and t.material_code = #{materialCode}
and t.status=#{status}
GROUP BY t.id

SELECT bm.id, bm.company_code, bm.id AS material_id, bm.category1_code, bm.category2_code, bm.category3_code, bm.material_name, bm.material_code, bm.purchase_unit_name, bm.purchase_unit_code, bm.stock_unit_code, bm.stock_unit_name, bm.STATUS, bm.material_code_name, bm.image_url, bm.category3_name AS categoryName, bm.ingredient, bm.gram_weight, bm.loss_rate, bm.supplier_factory_ingredient, bm.auxiliary_material, bm.remarks, bm.delivery_code, bm.delivery_name, bm.auxiliary_material, bm.source, bm.translate as translateDs FROM t_basicsdatum_material bm
join t_fabric_pool_item fpi on fpi.source_id = bm.id and fpi.del_flag = '0' join t_fabric_pool fp on fp.id = fpi.fabric_pool_id
${ ew.customSqlSegment}
