mapper.select:
SELECT t.*, t.color_name as colorName, t1.color16 colorHex, tb.color AS quote FROM t_basicsdatum_material_color t LEFT JOIN t_basicsdatum_colour_library t1 ON (t.company_code = t1.company_code AND t.color_code = t1.colour_code and t1.del_flag = '0') LEFT JOIN t_basicsdatum_material_price tb ON t.color_code LIKE CONCAT('%', tb.color, '%') WHERE t.company_code = #{companyCode} and t.del_flag = '0' and t.material_code = #{materialCode}
and t.status=#{status}
GROUP BY t.id

SELECT t.*, tb.width AS quote FROM t_basicsdatum_material_width t LEFT JOIN t_basicsdatum_specification t1 ON t.company_code = t1.company_code AND t.width_code = t1.`code` AND t1.del_flag = '0' LEFT JOIN t_basicsdatum_material_price tb ON tb.width LIKE CONCAT('%', t.width_code, '%') WHERE t.company_code = #{companyCode} and t.del_flag = '0' and t.material_code = #{materialCode}
and t.status=#{status}
GROUP BY t.id order by t.name

SELECT bm.id, bm.company_code, bm.id AS material_id, bm.category1_code, bm.category1_name, bm.category2_code, bm.category3_code, bm.material_name, bm.material_code, bm.purchase_unit_name, bm.purchase_unit_code, bm.stock_unit_code, bm.stock_unit_name, bm.STATUS, bm.material_code_name, bm.image_url, bm.category3_name AS categoryName, bm.ingredient, bm.gram_weight, bm.loss_rate, bm.factory_composition as supplierFactoryIngredient, bm.auxiliary_material, bm.remarks, bm.delivery_code, bm.delivery_name, bm.auxiliary_material, bm.source, bm.translate as translateDs, bm.is_protection FROM t_basicsdatum_material bm
join t_fabric_pool_item fpi on fpi.source_id = bm.id and fpi.del_flag = '0' join t_fabric_pool fp on fp.id = fpi.fabric_pool_id
${ ew.customSqlSegment}

SELECT
${ew.sqlSelect}
md5(uuid()) as ids, tbm.id, tbm.distribute, tbm.category1_code, tbm.category1_name, tbm.category2_code, tbm.category2_name, tbm.category3_code, tbm.category3_name, tbm.category_id, tbm.category_name, tbm.material_code_name, tbm.supplier_id, tbm.supplier_name, tbm.supplier_fabric_code, tbm.supplier_quotation_price, tbm.factory_composition, tbm.stock_unit_code, tbm.stock_unit_name, tbmp.order_day, tbm.specification, tbm.gram_weight, tbm.is_protection, tbmp.width_name, tbm.supplier_color_no, tbm.supplier_color_say, tbm.image_url, tbm.material_code, tbm.status, tbm.confirm_status, tbm.material_name, tbm.brand, tbm.brand_name, tbm.year, tbm.year_name, tbm.season, tbm.season_name, tbm.material_category, tbm.material_category_name, tbm.ingredient, tbm.proc_Mode, tbm.proc_mode_name, tbm.picking_method, tbm.picking_method_name, tbm.material_source, tbm.material_source_name, tbm.dev_name, tbm.purchase_dept, tbm.purchase_dept_name, tbm.purchase_id, tbm.purchase_name, tbm.total_inventory, tbm.inventory_availability, tbm.fabric_difficulty, tbm.fabric_difficulty_name, tbm.remarks, tbm.quality_level_name, tbm.purchase_unit_code, tbm.purchase_unit_name, tbm.purchase_convert_stock, tbm.ingredient_say, tbm.fabric_sale_point, tbm.embryonic_cycle, tbm.embryonic_free_cycle, tbm.replenishment_production_cycle, tbm.density, tbm.weave_type_name, tbm.embryo_type, tbm.embryo_type_name, tbm.fabric_property_type, tbm.fabric_property_type_name, tbm.auxiliary_material, tbm.check_bill_code, tbm.check_company_name, tbm.check_result, tbm.check_date, tbm.check_valid_date, tbm.check_items, tbm.check_order_user_id, tbm.check_order_user_name, tbm.fabric_difficulty_score_name, tbm.fabric_evaluation, tbm.risk_description, tbm.create_name, tbm.create_date, tbm.update_name, tbm.update_date, tbm.width_group, tbm.width_group_name, tbm.translate, tbm.fabric_test_flag, tbm.fabric_test_file_id, tbm.fabric_test_file_url, tbm.fabric_test_content, tbmc.color_code, tbmc.color_name, tbmp.quotation_price, tbmp.supplier_material_code, tbmc.supplier_color_code, (SELECT GROUP_CONCAT(tbmw.NAME) FROM t_basicsdatum_material_width tbmw WHERE tbmw.del_flag = 0 and tbmw.material_code = tbm.material_code GROUP BY tbmw.material_code) as widthName, temcic.remark as fabricEvaluation, temcic.company_full_name as checkCompanyName, temcic.check_date as checkDate, temcic.validity_time as checkValidDate, temcic.send_inspect_content as checkItems, temcic.maker_by_name as checkOrderUserName, temcic.file_url as checkFileUrl
FROM t_basicsdatum_material tbm LEFT JOIN t_basicsdatum_material_color tbmc on tbmc.material_code = tbm.material_code AND tbmc.del_flag='0' LEFT JOIN t_basicsdatum_material_price tbmp on tbmp.material_code = tbm.material_code AND select_flag ='1' AND tbmp.del_flag='0' LEFT JOIN t_escm_material_component_inspect_company temcic ON temcic.id = ( select id from t_escm_material_component_inspect_company where materials_no = tbm.material_code ORDER BY YEAR DESC, validity_time DESC limit 1) ${ew.customSqlSegment}

select
${ew.sqlSelect}
*
from (SELECT tpb.color as materialsColor, tpb.translate as materialsSpec, tpb.image_url as materialsImageUrl, tbs.supplier_abbreviation as supperSampleName, concat(ts.year, ts.season_name, ts.brand_name) as planningSeason, ts.prod_category1st_name as prodCategory1stName, ts.prod_category_name as prodCategoryName, tsc.style_no as bulkNo, tsc.design_no as designNo, tsc.color_specification as styleColor, tpb.material_code as materialsCode, ts.style_pic as stylePic, tsc.style_color_pic as styleColorPic, tsc.bom_status as bomStatus, tpb.id as packBomId, ts.id as styleId, tbm.category_id, tbm.category1_code, tbm.category2_code, tbm.category3_code, IF(tsc.bom_status = 0, '样品', '大货') as bomPhase, tpi.historical_data as historicalData, tpi.id as packId FROM t_style_color tsc INNER JOIN t_style ts ON tsc.style_id = ts.id and ts.del_flag = 0 INNER JOIN t_pack_info tpi ON ts.id = tpi.foreign_id and tsc.id = tpi.style_color_id and tpi.DEL_FLAG = 0 INNER JOIN t_pack_bom tpb ON tpb.foreign_id = tpi.id and tpb.del_flag = 0 INNER JOIN t_basicsdatum_material tbm ON tpb.material_id = tbm.id and tbm.DEL_FLAG = 0 INNER JOIN t_pack_bom_version tpbv ON tpb.bom_version_id = tpbv.id AND tpb.foreign_id = tpbv.foreign_id AND tpbv.status = 1 and tpbv.DEL_FLAG = 0 AND IF(tsc.bom_status = 0, 'packDesign', 'packBigGoods') = tpbv.pack_type LEFT JOIN t_basicsdatum_supplier tbs ON tpb.supplier_id = tbs.supplier_code) t ${ew.customSqlSegment}
