mapper.select:
select tfm.id, tfm.form_type_id, tfm.group_name, tfm.field_name, tfm.field_type, tfm.field_type_id, tfm.field_type_name, tfm.field_type_coding, tfm.default_hint, tfm.is_option, tfm.option_dict_key, tfm.is_must_fill, tfm.category_id, tfm.category_name, tfm.season, tfm.is_examine, tfm.status, tfm.sequence, tfm.field_explain, tfm.is_compile, tfm.form_object_id formObjectId, tfm.brand, tfm.brand_name, tfm.structure_tier from t_field_management tfm left join t_form_type tft on tfm.form_type_id = tft.id where tfm.company_code=#{companyCode} and tfm.del_flag='0' and tft.status='0'
and tfm.form_type_id=#{formTypeId}
and tfm.group_name=#{groupName}
and find_in_set(#{season},tfm.season)
and find_in_set(#{categoryId},tfm.category_id)
and tft.code=#{formTypeCode}
and tft.code=#{formTypeCode}
and tfm.field_name=#{fieldName}
and tfm.field_explain=#{fieldExplain}
and tfm.id in
#{id}
order By tfm.group_name desc ,tfm.create_date desc

select option_name optionName, id id, option_code optionCode, field_id fieldId from t_field_option where field_id in
#{id}
and del_flag='0'

select tfm.id, tfm.form_type_id, tfm.group_name, tfm.field_name, tfm.field_type, tfm.field_type_id, tfm.field_type_name, tfm.field_type_coding, tfm.default_hint, tfm.is_option, tfm.option_dict_key, tfm.is_must_fill, tfm.category_id, tfm.category_name, tfm.season, tpd.is_examine, tpd.design_show_flag, tpd.design_examine_flag, tpd.research_show_flag, tpd.research_examine_flag, tpd.replay_show_flag, tpd.replay_examine_flag, tpd.group_sort, tpd.sort, tpd.create_date, tfm.status, tfm.sequence, tfm.field_explain, tfm.is_compile, tfm.form_object_id formObjectId, tfm.brand, tfm.brand_name, tfm.structure_tier from t_field_management tfm left join t_form_type tft on tfm.form_type_id = tft.id left join t_planning_dimensionality tpd on ( tpd.del_flag='0' and tpd.channel = #{channel} and tpd.field_id = tfm.id and tpd.coefficient_flag = '1' and tpd.planning_season_id = #{planningSeasonId} and tpd.prod_category = #{prodCategory}) where tfm.company_code=#{companyCode} and tfm.del_flag='0' and tft.status='0'
and tfm.form_type_id=#{formTypeId}
and tfm.group_name=#{groupName}
and find_in_set(#{season},tfm.season)
and find_in_set(#{categoryId},tfm.category_id)
and tft.code=#{formTypeCode}
and tfm.id in
#{id}
ORDER BY tpd.group_sort ASC, tpd.sort ASC
