mapper.select:
SELECT
${ew.sqlSelect}
tobl.*, tob.name as orderBookName, ts.id as styleId, ts.design_no as style, ts.old_design_no, ts.style_pic, ts.brand_name, ts.brand as brandCode, ts.prod_category_name as categoryName, ts.prod_category as categoryCode, ts.prod_category2nd_name, ts.positioning as positioning, ts.positioning_name as positioningName, ts.designer as styleDesignerName, ts.designer_id as styleDesignerId, ts.pattern_design_name, ts.registering_no, ts.plate_positioning as patternPositioningCode, ts.plate_positioning_name as patternPositioningName, tsc.style_color_pic, tsc.style_no as bulkStyleNo, tsc.his_style_no as hisStyleNo, IFNULL(tobl.devt_type,ts.devt_type) as styleDevtType, ts.size_codes, (tsc.style_no, REGEXP "Q$") as high, tsc.band_name, tsc.band_code, tsc.color_name, tsc.color_code, tsc.supplier_abbreviation as fobClothingFactoryName, tsc.supplier_no as fobClothingFactoryCode, tsc.supplier as fobSupplier, tsc.tag_price, tsc.bom, tsc.bom_status, tsc.scm_send_flag, ts.planning_season_id, tbcl.colour_code as supplierColor, tpi.id as packInfoId, tpi.name as bomName, tob.channel as channel, tob.channel_name as channelName, IFNULL(tobl.material,0) * IFNULL(tsc.tag_price,0) as materialMoney,
ts.year_name, ts.season_name
FROM t_order_book_detail tobl left join t_style_color tsc on tobl.style_color_id = tsc.id and tsc.del_flag='0' left join t_style ts on ts.id = tsc.style_id and ts.del_flag='0' left join t_order_book tob on tob.id = tobl.order_book_id and tob.del_flag='0' left join t_basicsdatum_colour_library tbcl on tbcl.colour_code = tsc.color_code and tbcl.del_flag='0' LEFT JOIN t_pack_info tpi ON tsc.style_no = tpi.style_no where tobl.del_flag='0'
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}

SELECT tobl.order_book_id as orderBookId, count(tobl.order_book_id) as count FROM t_order_book_detail tobl where tobl.del_flag='0'
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
group by tobl.order_book_id
