pack_bom pb LEFT JOIN t_basicsdatum_material bm on bm.id = pb.material_id LEFT JOIN t_pack_bom_version pbv on pbv.id = pb.bom_version_id AND pbv.`status` = '1' LEFT JOIN t_pack_info pi ON pi.id = pb.foreign_id AND pi.del_flag = '0' LEFT JOIN t_style sd ON sd.id = pi.foreign_id AND sd.del_flag = '0' LEFT JOIN t_pattern_making pmm ON pmm.style_id = sd.id WHERE pb.pack_type = 'packDesign' AND pb.company_code = #{companyCode} AND pb.del_flag = '0' GROUP BY pi.style_no, sd.design_no ) AS bmid ON bmid.design_no_two = ssc.design_no AND ssc.style_no = bmid.style_no WHERE ssc.del_flag = '0' AND ssc.company_code = #{companyCode} AND sd.company_code = #{companyCode} AND uf.company_code = #{companyCode} AND bmid.material_id = #{materialId}
