mapper.select:
SELECT
${ew.sqlSelect}
p.id AS patternMakingId, p.pattern_design_id , p.pat_diff,p.pat_diff_name, p.sample_bar_code sampleBarCode,
CONCAT(s.year_name,s.season_name,s.brand_name) as season, s.style_pic, p.sample_type, p.pat_seq_name, (CASE WHEN p.technician_kitting = 0 THEN '否' ELSE '是' END) as technician_kitting, p.technician_kitting_date, p.pattern_req_date, p.demand_finish_date, p.shortage_remarks, p.revision_comments, p.revision_reason, s.pattern_design_name, p.requirement_num, p.suspend_remarks, p.pattern_making_score, p.cutter_name, s.design_no, p.cutter_finish_num, (CASE WHEN p.sgl_kitting = 0 THEN '否' ELSE '是' END) as sgl_kitting, p.stitcher_remark, p.stitcher, s.technician_name, p.pattern_technician_name, p.sample_pic, (CASE WHEN s.designer != '' THEN SUBSTRING_INDEX(s.designer, ',', 1) ELSE '' END) as designer, p.pattern_designer_name, s.merch_design_name, s.revised_design_name, s.reviewed_design_name, s.prod_category_name, s.prod_category2nd_name, s.prod_category3rd_name, p.pattern_finish_num, s.default_size, p.urgency_name, s.pattern_parts, p.sample_finish_num, p.pattern_making_quality_score, p.sample_making_score, p.sample_making_quality_score, p.second_processing, p.prm_send_date, (CASE WHEN p.prm_send_status = 0 THEN '未发送' ELSE '已发送' END) as prm_send_status, p.pattern_no, p.grading_name, p.grading_date, s.band_name, p.sample_bar_code, p.receive_sample_date, s.devt_type_name, (CASE WHEN p.break_off_Pattern = '1' THEN '打板中断' WHEN p.break_off_sample = '1' THEN '样衣中断' ELSE '启用' END) as pmStatus, p.refer_sample, p.break_off_Pattern, p.break_off_sample, p.status pStatus, p.create_date as `pmCreateDate`, p.demand_finish_date as demandFinishDate, p.del_flag
FROM t_style s LEFT JOIN t_pattern_making p ON ( s.id = p.style_id ) where p.del_flag = '0' and s.del_flag = '0'
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}

select CONCAT(node, '-', status) as nodeStatus, count(1) as total
from t_pattern_making ${ew.customSqlSegment}
group by node, status

select m.*, d.product_sizes, d.pat_diff as sd_pat_diff, d.pat_diff_name as sd_pat_diff_name, d.default_size
from t_pattern_making m
         join t_style d on (m.style_id = d.id) ${ew.customSqlSegment}

SELECT * FROM ( SELECT pk.sample_type, DATE_FORMAT( pk.create_date, IF(#{weeklyMonth} = 'week','%y年%u周', '%y年%m月') ) AS year_week, min( pk.create_date ) AS min_date, count( 1 ) AS num FROM t_pattern_making pk JOIN t_style sd ON sd.id = pk.style_id WHERE pk.del_flag = '0' AND pk.sample_type = '初版样' AND pk.company_code = #{companyCode} AND pk.create_date BETWEEN #{startTime} AND #{endTime}
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
GROUP BY DATE_FORMAT( pk.create_date, IF(#{weeklyMonth} = 'week','%y%u', '%y%m') ) ORDER BY year_week ASC ) AS pmc UNION SELECT * FROM ( SELECT pk.sample_type, DATE_FORMAT( pk.create_date, IF(#{weeklyMonth} = 'week','%y年%u周', '%y年%m月') ) AS year_week, min( pk.create_date ) AS min_date, count( 1 ) AS num FROM t_pattern_making pk JOIN t_style sd ON sd.id = pk.style_id WHERE pk.del_flag = '0' AND pk.sample_type = '改版样' AND pk.company_code = #{companyCode} AND pk.create_date BETWEEN #{startTime} AND #{endTime}
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
GROUP BY DATE_FORMAT( pk.create_date, IF(#{weeklyMonth} = 'week','%y%u', '%y%m') ) ORDER BY year_week ASC ) AS pmg

SELECT #{sampleType} AS sample_type, DATE_FORMAT( pmm.create_date, IF(#{weeklyMonth} = 'week','%y年%u周', '%y年%m月') ) AS year_week, min( pmm.create_date ) AS min_date, count( 1 ) AS num, SUM( pmm.requirement_num ) AS requirement_num_sum FROM ( SELECT sd.planning_category_id, pm.* FROM t_pattern_making pm LEFT JOIN t_style sd ON pm.style_id = sd.id LEFT JOIN t_planning_category_item pc ON sd.planning_category_id = pc.id WHERE pm.node = '打版任务' AND pm.company_code = #{companyCode} AND pm.create_date BETWEEN #{startTime} AND #{endTime}
AND pm.`status` IN
#{status}
AND pc.prod_category in
#{categoryId}
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
) pmm GROUP BY DATE_FORMAT( pmm.create_date, IF(#{weeklyMonth} = 'week','%y%u', '%y%m') ) ORDER BY year_week ASC;

SELECT ns.`status`, DATE_FORMAT( ns.start_date, IF(#{dto.weeklyMonth} = 'week','%y年%u周', '%y年%m月') ) AS year_week, min( ns.start_date ) AS min_date, count( 1 ) AS num FROM t_node_status ns LEFT JOIN t_pattern_making pm on ns.data_id = pm.id LEFT JOIN t_style s on pm.style_id = s.id WHERE pm.del_flag = '0' AND pm.company_code = #{dto.companyCode} AND ns.node = '样衣任务'
AND ns.`status` IN
#{status}
AND ns.start_date BETWEEN #{dto.startTime} AND #{dto.endTime}
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
GROUP BY ns.`status`,DATE_FORMAT( ns.start_date, IF(#{dto.weeklyMonth} = 'week','%y%u', '%y%m') ) ORDER BY year_week ASC ;

SELECT DATE_FORMAT( pm.create_date, IF ( #{dto.weeklyMonth} = 'week', '%y年%u周', '%y年%m月' ) ) AS year_week, sum( pm.requirement_num ) as requirement_num_sum FROM (SELECT pm.* FROM t_pattern_making pm INNER JOIN t_node_status ns on ns.data_id = pm.id AND ns.node = #{dto.node} WHERE pm.del_flag = '0' GROUP BY pm.id) AS pm LEFT JOIN t_style s on pm.style_id = s.id WHERE pm.del_flag = '0' AND pm.company_code = #{dto.companyCode} AND pm.create_date BETWEEN #{dto.startTime} AND #{dto.endTime}
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
GROUP BY DATE_FORMAT( pm.create_date, IF ( #{dto.weeklyMonth} = 'week', '%y%u', '%y%m' ) ) ORDER BY year_week ASC

SELECT DATE_FORMAT( pm.create_date, IF ( #{dto.weeklyMonth} = 'week', '%y年%u周', '%y年%m月' ) ) AS year_week, ROUND( sum( ( u.output / IF ( u.hours_within_unit = NULL OR u.hours_within_unit = 0, 1, u.hours_within_unit ) * 168 ) ), 2 ) as requirement_num_sum FROM (SELECT pm.* FROM t_pattern_making pm INNER JOIN t_node_status ns on ns.data_id = pm.id AND ns.node = #{dto.node} WHERE pm.del_flag = '0' GROUP BY pm.id) AS pm INNER JOIN ( SELECT DISTINCT ( substring_index( substring_index( a.user_ids,',', b.help_topic_id + 1 ), ',',- 1 ) ) AS user_id, a.* FROM ( SELECT * FROM `t_personnel_skills` ) AS a JOIN mysql.help_topic AS b ON b.help_topic_id < ( char_length( a.user_ids ) - char_length( REPLACE ( a.user_ids, ',', '' ) ) + 1 ) GROUP BY user_id ) u ON pm.pattern_design_id = u.user_id LEFT JOIN t_style s on pm.style_id = s.id WHERE pm.del_flag = '0' AND pm.company_code = #{dto.companyCode} AND pm.create_date BETWEEN #{dto.startTime} AND #{dto.endTime}
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
GROUP BY DATE_FORMAT( pm.create_date, IF ( #{dto.weeklyMonth} = 'week', '%y%u', '%y%m' ) ) ORDER BY year_week ASC
