mapper.select:
SELECT p.id as id, ps.pack_type as packType, p.code as code, p.prod_category as middleClassif, p.color as color, p.color_code as colorCode, CONCAT(p.design_no, p.style_name) as style, p.design_no as designNo, p.style_no as bulkStyleNo, sd.product_cost as targetCost, sd.devt_type_name as productionType, sd.style_pic as sampleDesignPic, sd.style_name as styleName, sd.style_flavour_name as productStyle, sd.designer as designer, sd.style_type_name as styleType, ssc.product_name as productName, GROUP_CONCAT( hti.type, '(', hti.percentage, ')' ) AS ingredient, ssc.tag_price as tagPrice, ssc.supplier as supplierName, ssc.supplier_abbreviation as supplierAbbreviation, ssc.style_color_pic as styleColorPic, ssob.meet_flag as meetFlag, sp.id as stylePricingId, sp.planning_rate as planningRatio, sp.control_confirm as planCostConfirm, sp.product_hangtag_confirm as productTagPriceConfirm, sp.control_hangtag_confirm as planTagPriceConfirm, sp.series, sp.series_name, ps.bom_status as bomStage, ht.down_content as downContent from t_pack_info p LEFT JOIN t_pack_info_status ps ON (p.id = ps.foreign_id and ps.del_flag = '0') left join t_style sd on (sd.id = p.foreign_id and sd.del_flag = '0') left join t_style_color ssc on (ssc.id = p.style_color_id and ssc.del_flag = '0') left join t_style_pricing sp on (sp.pack_id = p.id and sp.del_flag = '0') left join t_hang_tag ht on (ht.bulk_style_no = sd.style_no and ht.del_flag = '0') left join t_style_order_book_color ssobc on (ssobc.style_no = p.style_no and ssobc.del_flag = '0') LEFT JOIN t_hang_tag_ingredient hti ON ht.id = hti.hang_tag_id left join t_style_order_book ssob on (ssob.order_book_code = ssobc.order_book_code and ssob.del_flag = '0')
p.del_flag = '0'
and p.prod_category1st = #{dto.prodCategory1st}
and p.prod_category = #{dto.prodCategory}
and p.prod_category2nd = #{dto.prodCategory2nd}
and p.prod_category3rd = #{dto.prodCategory3rd}
and p.planning_season_id = #{dto.planningSeasonId}
and p.devt_type = #{dto.devtType}
and p.design_no like like CONCAT('%',#{dto.style},'%') or p.style_name like CONCAT('%',#{dto.style},'%')
and (p.design_no like CONCAT('%',#{dto.search},'%') or p.style_no like CONCAT('%',#{dto.search},'%') or p.style_name like CONCAT('%',#{dto.search},'%') )
and ht.product_name = #{dto.productName}
and sd.band_code = #{dto.bandCode}
and p.id = #{dto.packId}
group by p.id
