        CONCAT('%',#{dto.style},'%')


        and (p.design_no like CONCAT('%',#{dto.search},'%') or p.style_no like CONCAT('%',#{dto.search},'%')
        or
        p.style_name like CONCAT('%',#{dto.search},'%') )


