mapper.select:
select
${ew.sqlSelect}
from t_style_color tsc left join t_replay_rating trr on trr.foreign_id = tsc.id and trr.type = 'style' left join t_style ts on ts.id = tsc.style_id where tsc.del_flag = '0'
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}

select
${ew.sqlSelect}
from t_style_color tsc left join t_replay_rating trr on trr.foreign_id = tsc.id and trr.type = 'pattern' left join t_style ts on ts.id = tsc.style_id left join t_pattern_library tpl on tpl.style_id = ts.id AND tpl.del_flag = '0'
where tsc.del_flag = '0'
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}

select
${ew.sqlSelect}
from t_pack_bom tpb LEFT JOIN t_pack_info tpi ON tpb.foreign_id = tpi.id and tpi.del_flag = '0' and tpi.style_color_id is not null LEFT JOIN t_style ts ON tpi.foreign_id = ts.id AND ts.del_flag = '0' LEFT JOIN t_replay_rating trr ON trr.foreign_id = tpb.material_id AND trr.type = 'fabric' AND trr.del_flag = '0' where tpb.material_code != '0000' and tpb.pack_type = 'packBigGoods' and tpb.del_flag = '0' AND tpb.status = '1' AND tpb.category1_code = 'C4241'
and tpi.id is not null
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}

select
    count(0)
from t_style_color tsc
where tsc.del_flag = '0'

    and EXISTS(
        select 1 from t_style ts where ts.id = tsc.style_id and ts.del_flag = '0'
        and ${ ew.getSqlByAlias('ts')}
    )


    and EXISTS(
        select 1 from t_replay_rating trr where trr.foreign_id = tsc.id and trr.type = 'style' and trr.del_flag = '0'
        and ${ ew.getSqlByAlias('trr')}
    )


    and ${ ew.getSqlByAlias('tsc')}

select count(0) as count,

    group_concat(temp.bulkStyleNo) AS bulkStyleNo,

sum(temp.bulkUnitUse) AS bulkUnitUse
from (
select SQL_BIG_RESULT
    sum(tpb.bulk_unit_use) AS bulkUnitUse

        , group_concat(tpi.style_no) AS bulkStyleNo

from t_pack_bom tpb

    LEFT JOIN t_pack_info tpi ON tpb.foreign_id = tpi.id and tpi.del_flag = '0' and tpi.style_color_id is not null

where tpb.material_code != '0000'
and tpb.pack_type = 'packBigGoods'
and tpb.del_flag = '0'
AND tpb.status = '1'
AND tpb.category1_code = 'C4241'


        and ${ ew.getSqlByAlias('tpi')}


        and EXISTS(
        select 1 from t_pack_info tpi where tpb.foreign_id = tpi.id and tpi.del_flag = '0' and tpi.style_color_id is not null
        and ${ ew.getSqlByAlias('tpi')}
        )




    and EXISTS(
    select 1 from t_style ts
    left join t_pack_info tpi on tpi.foreign_id = ts.id and tpi.del_flag = '0' and tpi.style_color_id is not null
    where tpb.foreign_id = tpi.id and ts.del_flag = '0'
    and ${ ew.getSqlByAlias('ts')}
    )


    and EXISTS(
    select 1 from t_replay_rating trr where trr.foreign_id = tpb.material_id AND trr.type = 'fabric' AND trr.del_flag = '0'
    and ${ ew.getSqlByAlias('trr')}
    )


    and ${ ew.getSqlByAlias('tpb')}

group by tpb.material_code, tpb.foreign_id
) temp
