mapper.select:
SELECT
${ew.sqlSelect}
DISTINCT t.*, s.brand_name, s.year_name, s.season_name, s.month_name, s.band_name, s.design_no, p.style_no, p.color, p.prod_category_name, tht.technologist_name technologistName, tht.grading_name gradingName, s.style_pic, s.pattern_design_name, s.pattern_design_id, s.designer, s.designer_id, tpm.pat_seq as patSeq
FROM (t_style s, t_pre_production_sample_task t, t_pack_info p) left join t_pattern_making tpm ON (tpm.sample_bar_code = t.sample_bar_code and tpm.sample_bar_code!=null and tpm.sample_bar_code !='') left join t_hang_tag tht on (tht.bulk_style_no = p.style_no ) WHERE s.id = t.style_id and p.id=t.pack_info_id AND s.del_flag = '0' AND t.del_flag = '0'
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}

SELECT
${ew.sqlSelect}
DISTINCT t.*, s.brand_name, s.year_name, s.season_name, s.month_name, s.band_name, s.design_no, p.style_no, p.color, p.prod_category_name, s.style_pic, s.pattern_design_name, s.pattern_design_id, s.designer, s.designer_id, tpmbc.status, tpmbc.suggestion, tpmbc.suggestion_img, tpmbc.suggestion_video, tpmbc.update_date as confrimDate
FROM (t_style s, t_pre_production_sample_task_fob t) left join t_pattern_making_bar_code tpmbc where t.id = tpmbc.head_id and tpmbc.del_flag = '0' WHERE s.id = t.style_id and p.id=t.pack_info_id AND s.del_flag = '0' AND t.del_flag = '0'
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
