mapper.update:
update  t_style_group
set  style_no = #{nweStyleNo}
where style_no = #{styleNo} ;

update  t_style_group_color
set  style_no = #{nweStyleNo}
where style_no = #{styleNo} ;

update  t_hang_tag
set  bulk_style_no = #{nweStyleNo}
where bulk_style_no = #{styleNo} ;

update  t_pack_info
set  style_no = #{nweStyleNo}
where style_no = #{styleNo} ;

update t_style_main_accessories
set style_no = #{nweStyleNo}
where style_no = #{styleNo} ;

mapper.select:
select
sc.id as styleColorId, s.prod_category1st, s.prod_category1st_name, s.prod_category, s.prod_category_name, s.prod_category2nd, s.prod_category2nd_name, s.prod_category3rd, s.prod_category3rd_name, s.designer, s.design_no, s.his_design_no, s.designer_id, sc.style_id , sc.style_no , sc.style_color_pic as stylePic, sc.band_code, sc.band_name, fv.field_id, fv.field_name, fv.field_explain, fv.val, fv.val_name from t_style_color sc left join t_style s on (s.id=sc.style_id ) left join t_field_val fv on(fv.foreign_id=sc.id and fv.del_flag='0' and data_group='STYLE_COLOR') where s.del_flag='0' and sc.del_flag='0' and sc.order_flag='1'
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}

select count(1)
from t_style_color
where del_flag = '0' and style_no = #{styleNo}

select
${ew.sqlSelect}
concat(ts.year_name," ",ts.season_name," ",ts.brand_name) planningSeason, ts.task_level, ts.task_level_name, ts.style_type, ts.style_type_name, ts.size_range, ts.size_range_name, ts.merch_design_name, ts.pattern_design_name, ts.style_pic, ts.design_no as designNo, ts.prod_category1st, ts.prod_category1st_name, ts.prod_category, ts.prod_category_name, ts.his_design_no, ts.style_name, ts.style_flavour_name, ts.designer_id, IF(ts.designer != '', SUBSTRING_INDEX(ts.designer, ',', 1), '') as designer, ts.technician_id, ts.technician_name, ts.brand, ts.brand_name, ts.year, ts.year_name, ts.season, ts.season_name, ts.prod_category2nd_name, ts.prod_category2nd, ts.prod_category3rd as prodCategory3nd, ts.prod_category3rd_name as prodCategory3ndName, ts.id styleId, ts.design_audit_status, ts.design_marking_status, tsc.*, ts.devt_type as devtType, ts.devt_type_name as devtTypeName, CASE WHEN tsc.is_trim = '0' THEN tsma.style_no ELSE '' END as accessoryNo, CASE WHEN tsc.is_trim = '0' THEN tsma.color_name ELSE'' END as accessory1, CASE WHEN tsc.is_trim = '0' THEN ''ELSE tsma.style_no END as principalStyleNo, CASE WHEN tsc.is_trim = '0' THEN ''ELSE tsma.color_name END as principalStyle, tpi.id packInfoId, tpi.name infoName, tsp.control_confirm, tsp.product_hangtag_confirm, tsp.control_hangtag_confirm, -- tppst.technologist_name, ts.style_origin_name, concat(tsc.ware_code,tsc.color_code,tbs.sort) as defaultBarCode
FROM t_style ts LEFT JOIN t_style_color tsc ON (ts.id = tsc.style_id AND tsc.del_flag = '0') LEFT JOIN t_pack_info tpi on tpi.style_no = tsc.style_no LEFT JOIN t_style_pricing tsp on tsp.pack_id = tpi.id -- left join t_pre_production_sample_task tppst on tppst.pack_info_id = tpi.id left join (SELECT style_color_id ,GROUP_CONCAT(style_no) style_no ,GROUP_CONCAT(color_name) color_name ,is_trim FROM t_style_main_accessories WHERE del_flag='0' GROUP BY style_color_id) tsma on (tsma.style_color_id = tsc.id ) left join t_basicsdatum_size tbs ON FIND_IN_SET(tbs.id,ts.size_ids) > 0 and tbs.hangtags = ts.default_size ${ew.customSqlSegment}

select ts.*,tsc.style_no bulkyCargoNo from t_style ts
left join t_style_color tsc on ts.id = tsc.style_id and tsc.del_flag = '0'


        ${ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
