mapper.update:
update  t_style_group
set  style_no = #{nweStyleNo}
where style_no = #{styleNo} ;

update  t_style_group_color
set  style_no = #{nweStyleNo}
where style_no = #{styleNo} ;

update  t_style_order_book_color
set  style_no = #{nweStyleNo}
where style_no = #{styleNo} ;

update  t_pack_info
set  style_no = #{nweStyleNo}
where style_no = #{styleNo} ;

mapper.select:
select count(1)
from t_style_color
where style_id = #{styleNo}
