mapper.update:
update t_style
set design_no = #{newDesignNo}
where design_no = #{oldDesignNo};
update t_planning_category_item
set design_no = #{newDesignNo}
where design_no = #{oldDesignNo};
update t_pack_info
set design_no = #{newDesignNo}
where design_no = #{oldDesignNo};
update t_sample
set design_no = #{newDesignNo}
where design_no = #{oldDesignNo};
update t_style_color
set design_no = #{newDesignNo}
where design_no = #{oldDesignNo};

update t_pack_info
set devt_type     = #{devtType},
    devt_type_name=#{devtTypeName}
where foreign_id = #{styleId}
   or style_id = #{styleId}

mapper.select:
SELECT band_name AS product, STATUS AS dimension, count(id) AS total FROM t_style where del_flag = '0'
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
GROUP BY band_name, STATUS;

SELECT prod_category_name AS product, STATUS AS dimension, count(id) AS total FROM t_style where del_flag = '0'
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
GROUP BY prod_category_name, STATUS;

SELECT id, prod_category_name, design_no, his_design_no, price, seasonal, style_pic, continuation_point, designer, designer_id, band_name, style_no FROM
t_style sd WHERE sd.del_flag = '0' AND sd.del_flag = '0' and sd.prod_category is not null and sd.prod_category <> '' and sd.band_code is not null and sd.band_code <> ''
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}

SELECT prod_category1st, prod_category, prod_category2nd, count( 1 ) skc FROM t_style sd LEFT JOIN t_style_color c ON ( sd.id = c.style_id ) WHERE sd.del_flag = '0' and prod_category1st is not null and prod_category1st!='' and prod_category is not null and prod_category!='' and prod_category2nd is not null and prod_category2nd!=''
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
GROUP BY prod_category1st, prod_category, prod_category2nd order by prod_category1st, prod_category, prod_category2nd

select max(CONVERT(SUBSTR(old_design_no, ${pxLength}, ${length}), DECIMAL (11)))
from t_style
where brand = #{brand}
          and prod_category = #{category}
          and year = #{year}
  and company_code = #{companyCode}
