mapper.update:
update t_style
set design_no = #{newDesignNo}
where design_no = #{oldDesignNo};
update t_planning_category_item
set design_no = #{newDesignNo}
where design_no = #{oldDesignNo};
update t_pack_info
set design_no = #{newDesignNo}
where design_no = #{oldDesignNo};
update t_sample
set design_no = #{newDesignNo}
where design_no = #{oldDesignNo};
update t_style_color
set design_no = #{newDesignNo}
where design_no = #{oldDesignNo};

update t_pack_info
set devt_type     = #{devtType},
    devt_type_name=#{devtTypeName}
where foreign_id = #{styleId}
   or style_id = #{styleId}

mapper.select:
