        #{item}

    )

and sd.prod_category is not null and sd.prod_category <> ''
and sd.band_code is not null and sd.band_code <> ''

    ${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}

SELECT sd.id, prod_category_name, design_no, his_design_no, price, seasonal, style_pic, continuation_point, designer, designer_id, band_name, style_no FROM t_style sd left join t_field_val tfv on tfv.foreign_id = sd.id and tfv.del_flag = '0' WHERE sd.del_flag = '0' AND sd.del_flag = '0'
and exists(select 1 from t_field_val tfv where tfv.foreign_id = sd.id and tfv.del_flag = '0' and tfv.data_group = "SAMPLE_DESIGN_TECHNOLOGY" and tfv.val in
#{item}
)
and sd.prod_category is not null and sd.prod_category <> '' and sd.band_code is not null and sd.band_code <> ''
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}

SELECT sd.prod_category1st, sd.prod_category, sd.prod_category2nd, count( 1 ) skc, count( sd.STATUS = 2 or null) scriptedNum FROM t_style sd LEFT JOIN t_style_color c ON ( sd.id = c.style_id ) WHERE sd.del_flag = '0' and prod_category1st is not null and prod_category1st!='' and prod_category is not null and prod_category!='' and prod_category2nd is not null and prod_category2nd!=''
and exists(select 1 from t_field_val tfv where tfv.foreign_id = sd.id and tfv.del_flag = '0' and tfv.data_group = "SAMPLE_DESIGN_TECHNOLOGY" and tfv.val in
#{item}
)
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
GROUP BY prod_category1st, prod_category, prod_category2nd order by prod_category1st, prod_category, prod_category2nd

SELECT count(distinct sd.id) num FROM t_style sd LEFT JOIN t_pattern_making p ON ( sd.id = p.style_id ) and p.del_flag = 0 and p.disable_flag = 0 and p.sample_type = "初版样" WHERE sd.del_flag ='0'
and exists(select 1 from t_field_val tfv where tfv.foreign_id = sd.id and tfv.del_flag = '0' and tfv.data_group = "SAMPLE_DESIGN_TECHNOLOGY" and tfv.val in
#{item}
)
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}

